mapper.select:
WITH magazine_filtered AS ( SELECT a.artl_id, a.artl_name, a.artl_img, a.artl_link, a.artl_local, a.artl_pri_tag, a.artl_title, ROW_NUMBER() OVER (ORDER BY a.artl_id DESC) AS rn FROM ARTICLE a
LOWER(a.artl_name) LIKE '%' || LOWER(#{searchKeyword}) || '%' OR LOWER(a.artl_local) LIKE '%' || LOWER(#{searchKeyword}) || '%' OR LOWER(a.artl_pri_tag) LIKE '%' || LOWER(#{searchKeyword}) || '%' OR LOWER(a.artl_title) LIKE '%' || LOWER(#{searchKeyword}) || '%'
) SELECT * FROM magazine_filtered WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{pageSize}
